function x_dot = x_d_v(x, y, z, q_x, q_y, q_z, v_x, v_y, v_z, bg_x, bg_y, bg_z, ba_x, ba_y, ba_z, w_x, w_y, w_z, a_x, a_y, a_z, ng_x, ng_y, ng_z, na_x, na_y, na_z, nbg_x, nbg_y, nbg_z, nba_x, nba_y, nba_z)

x_dot = [v_x ; v_y ; v_z ; - (cos(q_y)*(bg_x + ng_x - w_x))/(cos(q_y)^2 + sin(q_y)^2) - (sin(q_y)*(bg_z + ng_z - w_z))/(cos(q_y)^2 + sin(q_y)^2) ; w_y - ng_y - bg_y + (cos(q_y)*sin(q_x)*(bg_z + ng_z - w_z))/(cos(q_x)*cos(q_y)^2 + cos(q_x)*sin(q_y)^2) - (sin(q_x)*sin(q_y)*(bg_x + ng_x - w_x))/(cos(q_x)*cos(q_y)^2 + cos(q_x)*sin(q_y)^2) ; (sin(q_y)*(bg_x + ng_x - w_x))/(cos(q_x)*cos(q_y)^2 + cos(q_x)*sin(q_y)^2) - (cos(q_y)*(bg_z + ng_z - w_z))/(cos(q_x)*cos(q_y)^2 + cos(q_x)*sin(q_y)^2) ; cos(q_x)*cos(q_z)*(ba_y - a_y + na_y) - (cos(q_z)*sin(q_y) + cos(q_y)*sin(q_x)*sin(q_z))*(ba_z - a_z + na_z) - (cos(q_y)*cos(q_z) - sin(q_x)*sin(q_y)*sin(q_z))*(ba_x - a_x + na_x) ; - (cos(q_y)*sin(q_z) + cos(q_z)*sin(q_x)*sin(q_y))*(ba_x - a_x + na_x) - (sin(q_y)*sin(q_z) - cos(q_y)*cos(q_z)*sin(q_x))*(ba_z - a_z + na_z) - cos(q_x)*cos(q_z)*(ba_y - a_y + na_y) ; cos(q_x)*sin(q_y)*(ba_x - a_x + na_x) - sin(q_x)*(ba_y - a_y + na_y) - cos(q_x)*cos(q_y)*(ba_z - a_z + na_z) - 981/100 ; nbg_x ; nbg_y ; nbg_z ; nba_x ; nba_y ; nba_z];

end